import React, { useEffect } from 'react';

const Typewriter = () => {
    const typewriterText = "What objects hold meaning to you?";
    let i = 0;

    function typeWriter() {
        if (i < typewriterText.length) {
            document.getElementById("typewriter").innerHTML += typewriterText.charAt(i);
            i++;
            setTimeout(typeWriter, 100); // Adjust typing speed here
        } else {
            setTimeout(deleteText, 2000); // Wait before starting to delete text
        }
    }

    function deleteText() {
        let currentText = document.getElementById("typewriter").innerHTML;
        if (currentText.length > 0) {
            document.getElementById("typewriter").innerHTML = currentText.substring(0, currentText.length - 1);
            setTimeout(deleteText, 50); // Adjust delete speed here
        } else {
            i = 0;
            typeWriter();
        }
    }

    useEffect(() => {
        typeWriter();
    }, []);

    return (
        <div>
            <p id="typewriter"></p>
            <audio id="typewriter-sound" src="typewriter-sound.mp3"></audio>
        </div>
    );
};

export default Typewriter;
