import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

function Preloader() {
  const navigate = useNavigate();
  const message = "What objects hold meaning to you?";
  const [typewriterText, setTypewriterText] = useState(message);
  const indexRef = useRef(message.length);

  useEffect(() => {
    const deleteWriter = () => {
      if (indexRef.current > 0) {
        setTypewriterText((prevText) => prevText.slice(0, -1));
        indexRef.current -= 1;
      } else {
        clearInterval(intervalId);
        // Navigate to home after the text is fully deleted
        setTimeout(() => {
          navigate('/home');
        }, 500); // Adjust the delay after deleting completes
      }
    };

    const intervalId = setInterval(deleteWriter, 100); // Adjust deleting speed here

    return () => clearInterval(intervalId);
  }, [navigate]);

  return (
    <div className="preloader">
      <h1>{typewriterText}</h1>
      {/* You can add more styling or a spinner here */}
    </div>
  );
}

export default Preloader;
