import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Preloader() {
  const navigate = useNavigate();
  const message = "What objects hold meaning to you?";
  const [typewriterText, setTypewriterText] = useState("");

  useEffect(() => {
    let i = 0;
    const typeWriter = () => {
      if (i < message.length) {
        setTypewriterText(prevText => prevText + message[i]);
        i++;
        setTimeout(typeWriter, 100); // Adjust typing speed here
      }
    };

    typeWriter(); // Start the typewriter effect when the component mounts

    // Adjust the timer duration to wait for the entire message to be typed out
    const timer = setTimeout(() => {
      navigate('/home');
    }, message.length * 100 + 500); // 100ms per character + 500ms delay

    return () => clearTimeout(timer);
  }, [message, navigate]);

  return (
    <div className="preloader">
      <h1>{typewriterText}</h1>
      {/* You can add more styling or a spinner here */}
    </div>
  );
}

export default Preloader;
