import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles.css'; // Make sure to import your CSS file

function Preloader() {
  const navigate = useNavigate();
  const message = "What objects hold meaning to you?";
  const [typewriterText, setTypewriterText] = useState("");
  const indexRef = useRef(0);
  const typingDone = useRef(false);
  const [fadeOut, setFadeOut] = useState(false);

  useEffect(() => {
    const typeWriter = () => {
      if (indexRef.current < message.length - 1) {
        setTypewriterText(prevText => prevText + message[indexRef.current]);
        indexRef.current += 1;
      } else {
        clearInterval(typingIntervalId);
        setTimeout(() => {
          typingDone.current = true;
          indexRef.current = message.length;
          deletingIntervalId = setInterval(deleteWriter, 100);
        }, 500);
      }
    };

    const deleteWriter = () => {
      if (indexRef.current > 0) {
        setTypewriterText(prevText => prevText.slice(0, -1));
        indexRef.current -= 1;
      } else {
        clearInterval(deletingIntervalId);
        setFadeOut(true);
        setTimeout(() => {
          navigate('/home');
        }, 1000); // Adjust the delay to match the fade-out transition duration
      }
    };

    let typingIntervalId = setInterval(typeWriter, 100);
    let deletingIntervalId;

    return () => {
      clearInterval(typingIntervalId);
      clearInterval(deletingIntervalId);
    };
  }, [message, navigate]);

  return (
    <div id="preloader" className={`fade-out ${fadeOut ? 'hidden' : ''}`}>
      <h1>{typewriterText}</h1>
    </div>
  );
}

export default Preloader;
