import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

function Preloader() {
  const navigate = useNavigate();
  const message = "What objects hold meaning to you?";
  const [typewriterText, setTypewriterText] = useState("");
  const indexRef = useRef(0);

  useEffect(() => {
    const typeWriter = () => {
      if (indexRef.current < message.length) {
        setTypewriterText((prevText) => prevText + message[indexRef.current]);
        indexRef.current += 1;
      } else {
        clearInterval(intervalId);
        // Navigate to home after the message is fully typed
        setTimeout(() => {
          navigate('/home');
        }, 500); // Adjust the delay after typing completes
      }
    };

    const intervalId = setInterval(typeWriter, 100); // Adjust typing speed here

    return () => clearInterval(intervalId);
  }, [message, navigate]);

  return (
    <div className="preloader">
      <h1>{typewriterText}</h1>
      {/* You can add more styling or a spinner here */}
    </div>
  );
}

export default Preloader;
