import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Preloader() {
  const navigate = useNavigate();
  const message = "What objects hold meaning to you?";
  const [typewriterText, setTypewriterText] = useState("");

  useEffect(() => {
    let i = 0;
    const typeWriter = () => {
        console.log(i)
        if (i < message.length) {
            setTypewriterText(prevText => prevText + message[i]);
            i++;
            setTimeout(typeWriter, 100); // Adjust typing speed here
        } else {
            // Set a timeout to navigate to home after the message is fully typed
            setTimeout(() => {
            navigate('/home');
            }, 500); // Adjust the delay after typing completes
        }
    };

    // typeWriter(); // Start the typewriter effect when the component mounts

    // Cleanup function to clear timeout if component unmounts before navigation
    return () => {
      clearTimeout(typeWriter);
    };
  }, [message, navigate]);

  return (
    <div className="preloader">
      <h1>{typewriterText}</h1>
      {/* You can add more styling or a spinner here */}
    </div>
  );
}

export default Preloader;
