// Filename - Home.jsx
import React, {useState, useEffect} from "react";
import Navbar from "./Navbar"
import { Link } from "react-router-dom";

export default function Home() {

    const [visible, setVisible] = useState(false);
    const sentences = [
        "Every time we travel somewhere else, we think about what items are most important when we pack.",
        "What items are most important to you?",
        "In exporing this question, we interviewed people about their most valuable object to learn more about the people around us."
    ]
    const [index, setIndex] = useState(0);
    const [text, setText] = useState("");
    const [textVis, setTextVis] = useState(false);

    console.log(index, text, textVis)
    useEffect(() => {
        const timeoutId = setTimeout(() => {
          setVisible(true);
        }, 100);

        return () => clearTimeout(timeoutId);
      }, []);

    useEffect(() => {
        if (index < sentences.length) {
            setTextVis(false)
            const fadeOutTimer = setTimeout(() => {
                setText(sentences[index])
                setTextVis(true)
            }, 1000);

            const displayTimer = setTimeout(() => {
                setTextVis(false);
                setIndex(prevIndex => prevIndex + 1)
            }, 4000)
            return () => {
                clearTimeout(fadeOutTimer);
                clearTimeout(displayTimer);
            }
        }
    }, [index])

    return (
        <div id="home" className={`fade-in ${visible ? 'visible' : ''}`}>
            <Navbar />
            <Link to="/Main" className="link">
                <button className="home--button">
                    &lt; explore the treasure box &gt;
                </button>
            </Link>
            <h3 id="home--text" className={`fade ${textVis ? 'visible' : ''}`}>{text}</h3>
        </div>
    )
}
